mapper.update:
UPDATE t_xt_order
SET appointment_time = #{appointmentTime}
WHERE order_no = #{orderNo}

update t_xt_order
set state = #{state}
where order_no = #{orderNo}

update t_xt_order
set job_no = #{jobNo}
where order_no = #{orderNo}

update t_xt_order
set state = 1, job_no = #{jobNo}
where order_no = #{orderNo}

update t_xt_order
set state = 2
where order_no = #{orderNo}

update t_xt_order
set state = 3
where order_no = #{orderNo}

update t_xt_order
set state = 6
where order_no = #{orderNo}

mapper.select:
SELECT * FROM t_xt_order
WHERE job_no = #{jobNo} AND state IN (5,11)
AND send_time >= #{startTime}
AND send_time <= #{endTime}
ORDER BY send_time DESC

mapper.update:
UPDATE t_xt_order
SET complete_time = now()
WHERE order_no = #{orderNo}

UPDATE t_xt_order
SET state = 11,pay_type = #{payType}
WHERE order_no = #{orderNo}

UPDATE t_xt_order
set is_return_visit = 1
WHERE order_no = #{orderNo}
